            alert('ë²ˆì—­ ê¸°ëŠ¥ì€ ì›ë³¸ ì˜¤ë””ì˜¤ê°€ í•„ìš”í•©ë‹ˆë‹¤. ìƒˆë¡œìš´ ì˜¤ë””ì˜¤ë¥¼ ì—…ë¡œë“œí•˜ê³  "Translate" íƒœìŠ¤í¬ë¥¼ ì„ íƒí•´ì£¼ì„¸ìš”.');
        } catch (err) {
            console.error('Translation error:', err);
