                )}

                {/* ê²°ê³¼ í‘œì‹œ */}
                {transcription && (
                    <div className="space-y-4">
                        {/* ì „ì‚¬ ê²°ê³¼ */}
                        <div className="bg-gray-50 rounded-lg p-6">
                            <div className="flex items-center justify-between mb-3">
                                <h3 className="text-lg font-semibold text-gray-900">ì „ì‚¬ ê²°ê³¼</h3>
