  const currentUserId = localStorage.getItem("userId");

  useEffect(() => {
    reloadChatRooms();

    // ê¸€ë¡œë²Œ WebSocket í´ë¼ì´ì–¸íŠ¸ ì´ˆê¸°í™”
    let globalClient = null;

    const initializeGlobalClient = async () => {
      try {
        globalClient = await initGlobalStompClient(
          (newRoom) => {
            console.log("ìƒˆ ë°© ì•Œë¦¼ ìˆ˜ì‹ :", newRoom);
            console.log("í˜„ì¬ ì‚¬ìš©ì ID:", currentUserId);
            console.log("ìƒˆë¡œ ìƒì„±ëœ ë°©ì˜ ì°¸ì—¬ìë“¤:", newRoom.participants);

            // ìƒˆë¡œìš´ ì±„íŒ…ë°©ì´ ìƒì„±ë˜ì—ˆì„ ë•Œ ì¦‰ì‹œ ëª©ë¡ ìƒˆë¡œê³ ì¹¨
            reloadChatRooms();

            // í˜„ì¬ ì„ íƒëœ ë°©ì´ ìˆë‹¤ë©´ ì´ˆê¸°í™” (ìƒˆë¡œìš´ ë°©ì´ ìƒì„±ë˜ì—ˆìœ¼ë¯€ë¡œ)
            setCurrentRoom(null);
          },
          (status, data) => {
            console.log("WebSocket ì—°ê²° ìƒíƒœ:", status, data);
          },
          (type, error) => {
            console.error("WebSocket ì—ëŸ¬:", type, error);
          }
        );
      } catch (error) {
        console.error("ê¸€ë¡œë²Œ í´ë¼ì´ì–¸íŠ¸ ì´ˆê¸°í™” ì‹¤íŒ¨:", error);
      }
    };

    initializeGlobalClient();

    return () => {
      if (globalClient) {
        globalClient.disconnect();
      }
    };
  }, []);

  const reloadChatRooms = async () => {
    try {
      const roomsData = await fetchChatRooms();
      setRooms(roomsData);
      console.log("ì±„íŒ…ë°© ëª©ë¡ ìƒˆë¡œê³ ì¹¨ ì™„ë£Œ:", roomsData.length, "ê°œ");
    } catch (error) {
      console.error("ì±„íŒ…ë°© ëª©ë¡ ìƒˆë¡œê³ ì¹¨ ì‹¤íŒ¨:", error);
    }
  };

  const handleNewMessage = ({ roomId, message, sentAt }) => {
    setRooms((prev) =>
      prev.map((r) =>
        r.roomId === roomId
          ? { ...r, lastMessage: message, lastMessageAt: sentAt }
          : r
      )
    );
  };

  const handleNewRoomCreated = () => {
    // ì±„íŒ…ë°© ìƒì„± í›„ ì¦‰ì‹œ ëª©ë¡ ìƒˆë¡œê³ ì¹¨
    reloadChatRooms();
  };

  const handleLeaveRoom = () => {
    // ì±„íŒ…ë°© ë‚˜ê°€ê¸° í›„ ëª©ë¡ ìƒˆë¡œê³ ì¹¨ ë° í˜„ì¬ ë°© ì´ˆê¸°í™”
    setCurrentRoom(null);
