import React, { useEffect, useState, useRef, useCallback } from "react";
import {
  fetchChatHistory,
  initStompClient,
  uploadChatImages,
  leaveChatRoom,
  sendTypingStatus,
  subscribeToTyping,
  unsubscribeFromTyping,
  markMessagesAsRead,
} from "../../api/chat";
import ChatHeader from "./ChatHeader";
import ChatMessageList from "./ChatMessageList";
import ChatInputArea from "./ChatInputArea";
import TypingIndicator from "./TypingIndicator";
import MessageSearch from "./MessageSearch";
import { useAlert } from "../../hooks/useAlert";

export default function ChatWindow({
  room,
  onNewMessage,
  currentUserId,
  onLeaveRoom,
}) {
  // Guard early return without affecting hooks order
  const isRoomMissing = !room;
  const { showError } = useAlert();

  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [selectedImageFiles, setSelectedImageFiles] = useState([]);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [typingUsers, setTypingUsers] = useState([]);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [highlightedMessageId, setHighlightedMessageId] = useState(null);
  const clientRef = useRef(null);
  const fileInputRef = useRef(null);
  const typingTimeoutRef = useRef(null);
  const typingSubscriptionRef = useRef(null);
  const isTypingSentRef = useRef(false);
  const messagesContainerRef = useRef(null);

  useEffect(() => {
    if (isRoomMissing) return undefined;

    const initializeChatRoom = async () => {
      try {
        // ì±„íŒ… íˆìŠ¤í† ë¦¬ ë¡œë“œ
        const history = await fetchChatHistory(room.roomId);
        setMessages(history);

        // WebSocket í´ë¼ì´ì–¸íŠ¸ ì´ˆê¸°í™”
        clientRef.current = await initStompClient(
          room.roomId,
          (msg) => {
            setMessages((prev) => [...prev, msg]);
            onNewMessage({
              roomId: room.roomId,
              message: msg.message,
              sentAt: msg.sentAt,
            });
          },
          (status, data) => {
            console.log(`ì±„íŒ…ë°© ${room.roomId} WebSocket ìƒíƒœ:`, status, data);
          },
          (type, error) => {
            console.error(`ì±„íŒ…ë°© ${room.roomId} WebSocket ì—ëŸ¬:`, type, error);
          }
        );

        // íƒ€ì´í•‘ ìƒíƒœ êµ¬ë…
        typingSubscriptionRef.current = subscribeToTyping(room.roomId, (typingData) => {
          const { userId, userName, userProfileImage, isTyping } = typingData;

          // ë³¸ì¸ì˜ íƒ€ì´í•‘ ìƒíƒœëŠ” ë¬´ì‹œ
          if (String(userId) === String(currentUserId)) {
            return;
          }

          setTypingUsers((prev) => {
            if (isTyping) {
              // ì´ë¯¸ ìˆëŠ” ì‚¬ìš©ìì¸ì§€ í™•ì¸
              const exists = prev.find(user => String(user.userId) === String(userId));
              if (!exists) {
                return [...prev, {
                  userId,
                  name: userName,
                  profileImage: userProfileImage
                }];
              }
              return prev;
            } else {
              // íƒ€ì´í•‘ ì¤‘ë‹¨ ì‹œ ëª©ë¡ì—ì„œ ì œê±°
              return prev.filter(user => String(user.userId) !== String(userId));
            }
          });
        });

        // ì±„íŒ…ë°© ì…ì¥ ì‹œ ë©”ì‹œì§€ë¥¼ ì½ìŒ ì²˜ë¦¬
        try {
          await markMessagesAsRead(room.roomId);
        } catch (error) {
          console.error("ë©”ì‹œì§€ ì½ìŒ ì²˜ë¦¬ ì‹¤íŒ¨:", error);
        }
      } catch (error) {
        console.error("ì±„íŒ…ë°© ì´ˆê¸°í™” ì‹¤íŒ¨:", error);
      }
    };

    initializeChatRoom();

    return () => {
      if (clientRef.current?.disconnect) {
        clientRef.current.disconnect();
      }
      if (typingSubscriptionRef.current) {
        unsubscribeFromTyping(typingSubscriptionRef.current);
      }
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
      // íƒ€ì´í•‘ ìƒíƒœ ì´ˆê¸°í™”
      setTypingUsers([]);
      isTypingSentRef.current = false;
    };
  }, [isRoomMissing, room?.roomId, onNewMessage]);

  // íƒ€ì´í•‘ ìƒíƒœ ê´€ë¦¬ í•¨ìˆ˜
  const handleTypingStart = useCallback(() => {
    if (!isTypingSentRef.current) {
      sendTypingStatus(room.roomId, true);
      isTypingSentRef.current = true;
    }

    // ê¸°ì¡´ íƒ€ì„ì•„ì›ƒ í´ë¦¬ì–´
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    // 3ì´ˆ í›„ íƒ€ì´í•‘ ì¤‘ë‹¨ ì‹ í˜¸ ì „ì†¡
    typingTimeoutRef.current = setTimeout(() => {
      sendTypingStatus(room.roomId, false);
      isTypingSentRef.current = false;
    }, 3000);
  }, [room.roomId]);

  const handleTypingStop = useCallback(() => {
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    if (isTypingSentRef.current) {
      sendTypingStatus(room.roomId, false);
      isTypingSentRef.current = false;
    }
  }, [room.roomId]);

  // ë©”ì‹œì§€ í•˜ì´ë¼ì´íŠ¸ ë° ìŠ¤í¬ë¡¤ ì´ë™
  const handleHighlightMessage = useCallback((message) => {
    setHighlightedMessageId(message.messageId);

    // 3ì´ˆ í›„ í•˜ì´ë¼ì´íŠ¸ í•´ì œ
    setTimeout(() => {
      setHighlightedMessageId(null);
    }, 3000);

    // í•´ë‹¹ ë©”ì‹œì§€ë¡œ ìŠ¤í¬ë¡¤
    setTimeout(() => {
      const messageElement = document.querySelector(`[data-message-id="${message.messageId}"]`);
      if (messageElement && messagesContainerRef.current) {
        messageElement.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }, 100);
  }, []);

  // í‚¤ë³´ë“œ ë‹¨ì¶•í‚¤ ì²˜ë¦¬
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl+F ë˜ëŠ” Cmd+Fë¡œ ê²€ìƒ‰ì°½ ì—´ê¸°
      if ((e.ctrlKey || e.metaKey) && e.key === 'f') {
        e.preventDefault();
        setIsSearchOpen(true);
      }
      // ESCë¡œ ê²€ìƒ‰ì°½ ë‹«ê¸°
      if (e.key === 'Escape' && isSearchOpen) {
        setIsSearchOpen(false);
        setHighlightedMessageId(null);
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isSearchOpen]);

  const handleLeaveRoom = async () => {
    try {
      await leaveChatRoom(room.roomId);
      if (onLeaveRoom) {
        onLeaveRoom();
      }
    } catch (error) {
      console.error("ì±„íŒ…ë°© ë‚˜ê°€ê¸° ì‹¤íŒ¨:", error);
      showError("ì±„íŒ…ë°© ë‚˜ê°€ê¸°ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.");
    }
  };

  const sendMessage = async (text, images, audioData) => {
    // íƒ€ì´í•‘ ìƒíƒœ ì¤‘ë‹¨
    handleTypingStop();

    let finalImageUrls = [];
    if (images.length) {
      try {
        finalImageUrls = await uploadChatImages(room.roomId, images);
      } catch {
        showError("ì´ë¯¸ì§€ ì—…ë¡œë“œ ì‹¤íŒ¨");
        return;
      }
    }

    clientRef.current.send(
      "/pub/chat/message",
      {},
      JSON.stringify({
        roomId: room.roomId,
        message: text.trim(),
        imageUrls: finalImageUrls,
        audioData: audioData, // Base64 Data URL
        messageType: audioData
          ? "AUDIO"
          : finalImageUrls.length
            ? "IMAGE"
            : text.trim()
              ? "TEXT"
              : "TEXT",
      })
    );

    setInput("");
    setSelectedImageFiles([]);
    setImagePreviews([]);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage(input, selectedImageFiles, null);
    }
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setSelectedImageFiles(files);
    setImagePreviews(files.map((f) => URL.createObjectURL(f)));
    e.target.value = "";
  };

  const removeImagePreview = (idx) => {
    setSelectedImageFiles((prev) => prev.filter((_, i) => i !== idx));
    setImagePreviews((prev) => prev.filter((_, i) => i !== idx));
  };

  if (isRoomMissing) return null;
  return (
    <div className="bg-white rounded-xl shadow-lg p-8 flex flex-col h-full w-full relative">
      {/* ë©”ì‹œì§€ ê²€ìƒ‰ */}
      <MessageSearch
        roomId={room.roomId}
        isOpen={isSearchOpen}
        onClose={() => {
          setIsSearchOpen(false);
          setHighlightedMessageId(null);
        }}
        messages={messages}
        onHighlightMessage={handleHighlightMessage}
      />

      <ChatHeader
        room={room}
        currentUserId={currentUserId}
        onLeaveRoom={handleLeaveRoom}
        onSearchToggle={() => setIsSearchOpen(!isSearchOpen)}
      />

      <div className="border-b border-gray-200 mx-6 my-4" />

      <div className="flex-1 min-h-0 flex flex-col">
        <ChatMessageList
          messages={messages}
          currentUserId={currentUserId}
          highlightedMessageId={highlightedMessageId}
          containerRef={messagesContainerRef}
          formatTimestamp={(dateStr) => {
            const date = new Date(dateStr);
            const now = new Date();
            const diffInHours = (now - date) / (1000 * 60 * 60);

            if (diffInHours < 24) {
              return date.toLocaleTimeString("ko-KR", {
                hour: "2-digit",
                minute: "2-digit",
                hour12: false,
              });
            } else {
              return date.toLocaleDateString("ko-KR", {
                month: "short",
                day: "numeric",
                hour: "2-digit",
                minute: "2-digit",
                hour12: false,
              });
            }
          }}
        />

        {/* íƒ€ì´í•‘ ì¸ë””ì¼€ì´í„° */}
        <TypingIndicator typingUsers={typingUsers} />
      </div>

      <ChatInputArea
        input={input}
        setInput={setInput}
        sendMessage={sendMessage}
        showEmojiPicker={showEmojiPicker}
        setShowEmojiPicker={setShowEmojiPicker}
        selectedImageFiles={selectedImageFiles}
        imagePreviews={imagePreviews}
        handleFileChange={handleFileChange}
        removeImagePreview={removeImagePreview}
        fileInputRef={fileInputRef}
        onTypingStart={handleTypingStart}
        onTypingStop={handleTypingStop}
      />
    </div>
  );
}

import React, { useState, useEffect, useRef } from 'react';
import { Search, X, ChevronUp, ChevronDown } from 'lucide-react';
import { searchChatMessages } from '../../api/chat';

const MessageSearch = ({ roomId, isOpen, onClose, messages, onHighlightMessage }) => {
  const [keyword, setKeyword] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(-1);
  const [isLoading, setIsLoading] = useState(false);
  const [totalResults, setTotalResults] = useState(0);
  const inputRef = useRef(null);
  const searchTimeoutRef = useRef(null);

  // ê²€ìƒ‰ì°½ì´ ì—´ë¦´ ë•Œ í¬ì»¤ìŠ¤
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  // í‚¤ì›Œë“œ ë³€ê²½ ì‹œ ê²€ìƒ‰ ì‹¤í–‰ (ë””ë°”ìš´ì‹±)
  useEffect(() => {
    if (searchTimeoutRef.current) {
      clearTimeout(searchTimeoutRef.current);
    }

    if (keyword.trim().length >= 2) {
      searchTimeoutRef.current = setTimeout(() => {
        performSearch();
      }, 300);
    } else {
      setSearchResults([]);
      setCurrentIndex(-1);
      setTotalResults(0);
    }

    return () => {
      if (searchTimeoutRef.current) {
        clearTimeout(searchTimeoutRef.current);
      }
    };
  }, [keyword, roomId]);

  const performSearch = async () => {
    if (!keyword.trim() || !roomId) return;

    setIsLoading(true);
    try {
      // ì„œë²„ ê²€ìƒ‰
      const serverResults = await searchChatMessages(roomId, keyword.trim());

      // ë¡œì»¬ ë©”ì‹œì§€ì—ì„œë„ ê²€ìƒ‰ (ì‹¤ì‹œê°„ ë©”ì‹œì§€)
      const localResults = messages.filter(msg => 
        msg.message && msg.message.toLowerCase().includes(keyword.toLowerCase())
      );

      // ê²°ê³¼ í•©ì¹˜ê¸° (ì¤‘ë³µ ì œê±°)
      const allResults = [...serverResults.content];
      localResults.forEach(local => {
        if (!allResults.find(server => server.messageId === local.messageId)) {
          allResults.push(local);
        }
      });

      // ì‹œê°„ìˆœ ì •ë ¬ (ìµœì‹ ìˆœ)
      allResults.sort((a, b) => new Date(b.sentAt) - new Date(a.sentAt));

      setSearchResults(allResults);
      setTotalResults(allResults.length);
      setCurrentIndex(allResults.length > 0 ? 0 : -1);

      // ì²« ë²ˆì§¸ ê²°ê³¼ë¡œ ì´ë™
      if (allResults.length > 0) {
        onHighlightMessage(allResults[0]);
      }
    } catch (error) {
      console.error('ê²€ìƒ‰ ì‹¤íŒ¨:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const navigateResult = (direction) => {
    if (searchResults.length === 0) return;

    let newIndex;
    if (direction === 'up') {
      newIndex = currentIndex > 0 ? currentIndex - 1 : searchResults.length - 1;
    } else {
      newIndex = currentIndex < searchResults.length - 1 ? currentIndex + 1 : 0;
    }

    setCurrentIndex(newIndex);
    onHighlightMessage(searchResults[newIndex]);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      onClose();
    } else if (e.key === 'Enter') {
      e.preventDefault();
      if (e.shiftKey) {
        navigateResult('up');
      } else {
        navigateResult('down');
      }
    }
  };

  const formatTimestamp = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleString('ko-KR', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      hour12: false
    });
  };

  const highlightKeyword = (text, keyword) => {
    if (!keyword) return text;

    const regex = new RegExp(`(${keyword})`, 'gi');
    return text.split(regex).map((part, index) => 
      regex.test(part) ? (
        <mark key={index} className="bg-yellow-200 text-gray-900 px-1 rounded">
          {part}
        </mark>
      ) : part
    );
  };

  if (!isOpen) return null;

  return (
    <div className="absolute top-0 left-0 right-0 z-10 bg-white border-b border-gray-200 shadow-lg">
      <div className="p-4">
        {/* ê²€ìƒ‰ ì…ë ¥ì°½ */}
        <div className="flex items-center gap-2 mb-3">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              ref={inputRef}
              type="text"
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="ë©”ì‹œì§€ ê²€ìƒ‰..."
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#00C471] focus:border-transparent"
            />
            {isLoading && (
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                <div className="w-4 h-4 border-2 border-gray-300 border-t-[#00C471] rounded-full animate-spin"></div>
              </div>
            )}
          </div>

          {/* ë„¤ë¹„ê²Œì´ì…˜ ë²„íŠ¼ë“¤ */}
          <div className="flex items-center gap-1">
            <span className="text-sm text-gray-500 px-2">
              {totalResults > 0 ? `${currentIndex + 1}/${totalResults}` : '0'}
            </span>
            <button
              onClick={() => navigateResult('up')}
              disabled={searchResults.length === 0}
              className="p-1 rounded hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ChevronUp className="w-4 h-4" />
            </button>
            <button
              onClick={() => navigateResult('down')}
              disabled={searchResults.length === 0}
              className="p-1 rounded hover:bg-gray-100 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ChevronDown className="w-4 h-4" />
            </button>
          </div>

          <button
            onClick={onClose}
            className="p-1 rounded hover:bg-gray-100 text-gray-500"
          >
            <X className="w-4 h-4" />
          </button>
        </div>

        {/* ê²€ìƒ‰ ê²°ê³¼ ë¯¸ë¦¬ë³´ê¸° */}
        {keyword.length >= 2 && searchResults.length > 0 && (
          <div className="max-h-32 overflow-y-auto bg-gray-50 rounded-lg">
            {searchResults.slice(0, 5).map((result, index) => (
              <button
                key={result.messageId}
                onClick={() => {
                  setCurrentIndex(index);
                  onHighlightMessage(result);
                }}
                className={`w-full text-left p-2 hover:bg-gray-100 border-b border-gray-200 last:border-b-0 ${
                  index === currentIndex ? 'bg-blue-50 border-blue-200' : ''
                }`}
              >
                <div className="flex items-center justify-between mb-1">
                  <span className="text-sm font-medium text-gray-700">
                    {result.sender.name}
                  </span>
                  <span className="text-xs text-gray-500">
                    {formatTimestamp(result.sentAt)}
                  </span>
                </div>
                <p className="text-sm text-gray-600 truncate">
                  {highlightKeyword(result.message, keyword)}
                </p>
              </button>
            ))}
            {searchResults.length > 5 && (
              <div className="p-2 text-center text-xs text-gray-500">
                +{searchResults.length - 5}ê°œ ë”
              </div>
            )}
          </div>
        )}

        {/* ê²€ìƒ‰ ë„ì›€ë§ */}
        {keyword.length === 0 && (
          <div className="text-sm text-gray-500">
            <p>ë©”ì‹œì§€ë¥¼ ê²€ìƒ‰í•˜ì„¸ìš”. (ìµœì†Œ 2ê¸€ì)</p>
            <p className="text-xs mt-1">Enter: ë‹¤ìŒ ê²°ê³¼, Shift+Enter: ì´ì „ ê²°ê³¼, Esc: ë‹«ê¸°</p>
          </div>
        )}

        {keyword.length >= 2 && searchResults.length === 0 && !isLoading && (
          <div className="text-sm text-gray-500 text-center py-4">
            ê²€ìƒ‰ ê²°ê³¼ê°€ ì—†ìŠµë‹ˆë‹¤.
          </div>
        )}
      </div>
    </div>
  );
};

export default MessageSearch;

import { useState, useRef, useEffect } from 'react';
import { Play, Pause, Mic } from 'lucide-react';

export default function VoiceMessage({ audioUrl, duration, isMine }) {
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentTime, setCurrentTime] = useState(0);
    const [totalDuration, setTotalDuration] = useState(duration || 0);
    const [waveformData, setWaveformData] = useState([]);
    const audioRef = useRef(null);
    const animationRef = useRef(null);

    useEffect(() => {
        // ë”ë¯¸ íŒŒí˜• ë°ì´í„° ìƒì„± (ì‹¤ì œë¡œëŠ” ì˜¤ë””ì˜¤ ë¶„ì„ í•„ìš”)
        const bars = Array.from({ length: 40 }, () => Math.random() * 0.8 + 0.2);
        setWaveformData(bars);
    }, []);

    useEffect(() => {
        const audio = audioRef.current;
        if (!audio) return;

        const handleLoadedMetadata = () => {
            setTotalDuration(audio.duration);
        };

        const handleTimeUpdate = () => {
            setCurrentTime(audio.currentTime);
        };

        const handleEnded = () => {
            setIsPlaying(false);
            setCurrentTime(0);
        };

        audio.addEventListener('loadedmetadata', handleLoadedMetadata);
        audio.addEventListener('timeupdate', handleTimeUpdate);
        audio.addEventListener('ended', handleEnded);

        return () => {
            audio.removeEventListener('loadedmetadata', handleLoadedMetadata);
            audio.removeEventListener('timeupdate', handleTimeUpdate);
            audio.removeEventListener('ended', handleEnded);
        };
    }, []);

    const togglePlay = () => {
        const audio = audioRef.current;
        if (!audio) return;

        if (isPlaying) {
            audio.pause();
            setIsPlaying(false);
        } else {
            audio.play();
            setIsPlaying(true);
        }
    };

    const formatTime = (seconds) => {
        if (!seconds || isNaN(seconds)) return '0:00';
        const mins = Math.floor(seconds / 60);
        const secs = Math.floor(seconds % 60);
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    };

    const handleWaveformClick = (e) => {
        const audio = audioRef.current;
        if (!audio || !totalDuration) return;

        const rect = e.currentTarget.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const percentage = x / rect.width;
        const newTime = percentage * totalDuration;

        audio.currentTime = newTime;
        setCurrentTime(newTime);
    };

    const progressPercentage = totalDuration ? (currentTime / totalDuration) * 100 : 0;

    return (
        <div className={`flex items-center gap-3 p-3 rounded-lg ${isMine ? 'bg-[#00C471]' : 'bg-[#FFE4EC]'
            } min-w-[280px] max-w-[320px]`}>
            {/* Play/Pause Button */}
            <button
                onClick={togglePlay}
                className={`flex-shrink-0 w-10 h-10 rounded-full flex items-center justify-center ${isMine
                        ? 'bg-white/20 hover:bg-white/30 text-white'
                        : 'bg-gray-800/10 hover:bg-gray-800/20 text-gray-800'
                    } transition-colors`}
            >
                {isPlaying ? (
                    <Pause className="w-5 h-5" />
                ) : (
                    <Play className="w-5 h-5 ml-0.5" />
                )}
            </button>

            {/* Waveform */}
            <div className="flex-1 flex flex-col gap-1">
                <div
                    className="relative h-10 flex items-center cursor-pointer"
                    onClick={handleWaveformClick}
                >
                    {/* Waveform bars */}
                    <div className="absolute inset-0 flex items-center gap-[2px]">
                        {waveformData.map((height, index) => {
                            const barPercentage = (index / waveformData.length) * 100;
                            const isActive = barPercentage <= progressPercentage;

                            return (
                                <div
                                    key={index}
                                    className={`flex-1 rounded-full transition-all duration-75 ${isMine
                                            ? isActive ? 'bg-white' : 'bg-white/40'
                                            : isActive ? 'bg-gray-800' : 'bg-gray-800/30'
                                        }`}
                                    style={{ height: `${height * 100}%` }}
                                />
                            );
                        })}
                    </div>
                </div>

                {/* Time */}
                <div className={`flex items-center justify-between text-xs ${isMine ? 'text-white/80' : 'text-gray-600'
                    }`}>
                    <span>{formatTime(currentTime)}</span>
                    <span>{formatTime(totalDuration)}</span>
                </div>
            </div>

            {/* Voice icon */}
            <div className={`flex-shrink-0 ${isMine ? 'text-white/60' : 'text-gray-600'
                }`}>
                <Mic className="w-4 h-4" />
            </div>

            {/* Hidden audio element */}
            <audio
                ref={audioRef}
                src={audioUrl}
                preload="metadata"
                crossOrigin="anonymous"
            />
        </div>
    );
}

