import React, { useState, useRef, useEffect } from 'react';
import { useFocusManagement } from '../../hooks/useKeyboardNavigation';
import { createListItemAriaProps, createKeyboardHint } from '../../utils/accessibility';

/**
 * í‚¤ë³´ë“œ ë„¤ë¹„ê²Œì´ì…˜ ê°€ëŠ¥í•œ ë¦¬ìŠ¤íŠ¸ ì»´í¬ë„ŒíŠ¸
 * @param {Object} props
 * @param {Array} props.items - ë¦¬ìŠ¤íŠ¸ ì•„ì´í…œ ë°°ì—´
 * @param {Function} props.renderItem - ì•„ì´í…œ ë Œë”ë§ í•¨ìˆ˜ (item, index, isActive) => ReactNode
 * @param {Function} props.onItemSelect - ì•„ì´í…œ ì„ íƒ í•¸ë“¤ëŸ¬
 * @param {Function} props.onItemActivate - ì•„ì´í…œ í™œì„±í™” í•¸ë“¤ëŸ¬ (Enter/Space)
 * @param {number} props.initialIndex - ì´ˆê¸° í™œì„± ì¸ë±ìŠ¤
 * @param {boolean} props.loop - ìˆœí™˜ ë„¤ë¹„ê²Œì´ì…˜ ì—¬ë¶€
 * @param {boolean} props.autoFocus - ìë™ í¬ì»¤ìŠ¤ ì—¬ë¶€
 * @param {string} props.className - ì¶”ê°€ CSS í´ë˜ìŠ¤
 * @param {string} props.itemClassName - ì•„ì´í…œ CSS í´ë˜ìŠ¤
 */
const KeyboardNavigableList = ({
  items = [],
  renderItem,
  onItemSelect,
  onItemActivate,
  initialIndex = 0,
  loop = true,
  autoFocus = false,
  className = '',
  itemClassName = ''
}) => {
  const [activeIndex, setActiveIndex] = useState(initialIndex);
  const listRef = useRef(null);
  const itemRefs = useRef([]);

  // ì•„ì´í…œ ref ë°°ì—´ ì´ˆê¸°í™”
  useEffect(() => {
    itemRefs.current = items.map((_, index) => itemRefs.current[index] || React.createRef());
  }, [items.length]);

  // í¬ì»¤ìŠ¤ ê´€ë¦¬
  const {
    focusNext,
    focusPrevious,
    focusIndex,
    focusFirst,
    focusLast
  } = useFocusManagement({
    focusableElements: itemRefs.current,
    initialIndex,
    loop,
    autoFocus
  });

  // í‚¤ë³´ë“œ ì´ë²¤íŠ¸ ì²˜ë¦¬
  const handleKeyDown = (e) => {
    switch (e.key) {
      case 'ArrowDown': {
        e.preventDefault();
        const nextIndex = loop 
          ? (activeIndex + 1) % items.length
          : Math.min(activeIndex + 1, items.length - 1);
        setActiveIndex(nextIndex);
        focusNext();
        onItemSelect?.(items[nextIndex], nextIndex);
        break;
      }

      case 'ArrowUp': {
        e.preventDefault();
        const prevIndex = loop 
          ? (activeIndex - 1 + items.length) % items.length
          : Math.max(activeIndex - 1, 0);
        setActiveIndex(prevIndex);
        focusPrevious();
        onItemSelect?.(items[prevIndex], prevIndex);
        break;
      }

      case 'Home': {
        e.preventDefault();
        setActiveIndex(0);
        focusFirst();
        onItemSelect?.(items[0], 0);
        break;
      }

      case 'End': {
        e.preventDefault();
        const lastIndex = items.length - 1;
        setActiveIndex(lastIndex);
        focusLast();
        onItemSelect?.(items[lastIndex], lastIndex);
        break;
      }

      case 'Enter': 
      case ' ': {
        e.preventDefault();
        onItemActivate?.(items[activeIndex], activeIndex);
        break;
      }
    }
  };

  // ë§ˆìš°ìŠ¤ í´ë¦­ ì²˜ë¦¬
  const handleItemClick = (item, index) => {
    setActiveIndex(index);
    focusIndex(index);
    onItemSelect?.(item, index);
    onItemActivate?.(item, index);
  };

  // ë§ˆìš°ìŠ¤ í˜¸ë²„ ì²˜ë¦¬
  const handleItemMouseEnter = (index) => {
    setActiveIndex(index);
  };

  if (!items.length) {
    return (
      <div 
        className={`text-center py-8 text-gray-500 ${className}`}
        role="status"
      >
        í‘œì‹œí•  í•­ëª©ì´ ì—†ìŠµë‹ˆë‹¤
      </div>
    );
  }

  return (
    <div
      ref={listRef}
      className={`focus:outline-none ${className}`}
      role="listbox"
      tabIndex={0}
      onKeyDown={handleKeyDown}
      aria-label={`ëª©ë¡, ${createKeyboardHint('â†‘ â†“', 'í•­ëª© ì´ë™')}, ${createKeyboardHint('Enter', 'ì„ íƒ')}`}
      aria-activedescendant={`list-item-${activeIndex}`}
    >
      {items.map((item, index) => {
        const isActive = index === activeIndex;
        const listItemProps = createListItemAriaProps(index + 1, items.length);

        return (
          <div
            key={index}
            ref={itemRefs.current[index]}
            id={`list-item-${index}`}
            className={`cursor-pointer transition-colors duration-150 focus:outline-none ${
              isActive 
                ? 'bg-green-50 border-green-500 ring-2 ring-green-500 ring-opacity-50' 
                : 'hover:bg-gray-50'
            } ${itemClassName}`}
            tabIndex={-1}
            onClick={() => handleItemClick(item, index)}
            onMouseEnter={() => handleItemMouseEnter(index)}
            {...listItemProps}
            aria-selected={isActive}
          >
            {renderItem ? renderItem(item, index, isActive) : (
              <div className="p-3">
                {typeof item === 'string' ? item : JSON.stringify(item)}
              </div>
            )}
          </div>
        );
      })}

      {/* ìŠ¤í¬ë¦° ë¦¬ë”ìš© ì‚¬ìš©ë²• ì•ˆë‚´ */}
      <div className="sr-only" aria-live="polite">
        {items.length}ê°œ í•­ëª© ì¤‘ {activeIndex + 1}ë²ˆì§¸ê°€ ì„ íƒë˜ì—ˆìŠµë‹ˆë‹¤. 
        ìœ„ì•„ë˜ í™”ì‚´í‘œ í‚¤ë¡œ ì´ë™í•˜ê³  Enter í‚¤ë¡œ ì„ íƒí•˜ì„¸ìš”.
      </div>
    </div>
  );
};

