import { useState, useCallback } from 'react';

/**
 * íŒŒì¼ ì—…ë¡œë“œë¥¼ ìœ„í•œ ì»¤ìŠ¤í…€ í›…
 * ì‹¤ì œ ì§„í–‰ë¥  ì¶”ì ê³¼ ì—ëŸ¬ ì²˜ë¦¬ë¥¼ í¬í•¨
 * @param {Object} options - ì—…ë¡œë“œ ì˜µì…˜
 * @returns {Object} - ì—…ë¡œë“œ ìƒíƒœì™€ í•¨ìˆ˜ë“¤
 */
export function useFileUpload(options = {}) {
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);

  const resetState = useCallback(() => {
    setIsUploading(false);
    setUploadProgress(0);
    setError(null);
    setUploadedFile(null);
  }, []);

  /**
   * XMLHttpRequestë¥¼ ì‚¬ìš©í•œ ì‹¤ì œ ì§„í–‰ë¥  ì¶”ì  ì—…ë¡œë“œ
   * @param {string} url - ì—…ë¡œë“œ URL
   * @param {FormData} formData - í¼ ë°ì´í„°
   * @param {Object} headers - ì¶”ê°€ í—¤ë”
   * @returns {Promise<Object>} - ì—…ë¡œë“œ ê²°ê³¼
   */
  const uploadWithProgress = useCallback(async (url, formData, headers = {}) => {
    return new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();

      // ì—…ë¡œë“œ ì§„í–‰ë¥  ì¶”ì 
      xhr.upload.onprogress = (event) => {
        if (event.lengthComputable) {
          const progress = Math.round((event.loaded / event.total) * 100);
          setUploadProgress(progress);
        }
      };

      // ì—…ë¡œë“œ ì™„ë£Œ
      xhr.onload = () => {
        if (xhr.status >= 200 && xhr.status < 300) {
          try {
            const result = JSON.parse(xhr.responseText);
            setUploadProgress(100);
            resolve(result);
          } catch (e) {
            reject(new Error('ì‘ë‹µ íŒŒì‹± ì‹¤íŒ¨'));
          }
        } else {
          try {
            const errorData = JSON.parse(xhr.responseText);
            reject(new Error(errorData.message || `ì—…ë¡œë“œ ì‹¤íŒ¨: ${xhr.status}`));
          } catch (e) {
            reject(new Error(`ì—…ë¡œë“œ ì‹¤íŒ¨: ${xhr.status} ${xhr.statusText}`));
          }
        }
      };

      // ì—ëŸ¬ ì²˜ë¦¬
      xhr.onerror = () => {
        reject(new Error('ë„¤íŠ¸ì›Œí¬ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.'));
      };

      // ì—…ë¡œë“œ ì·¨ì†Œ
      xhr.onabort = () => {
        reject(new Error('ì—…ë¡œë“œê°€ ì·¨ì†Œë˜ì—ˆìŠµë‹ˆë‹¤.'));
      };

      // ìš”ì²­ ì‹œì‘
      xhr.open('POST', url);

      // í—¤ë” ì„¤ì •
      Object.entries(headers).forEach(([key, value]) => {
        xhr.setRequestHeader(key, value);
      });

      xhr.send(formData);
    });
  }, []);

  /**
   * íŒŒì¼ ì—…ë¡œë“œ ì‹¤í–‰
   * @param {string} url - ì—…ë¡œë“œ URL
   * @param {FormData} formData - í¼ ë°ì´í„°
   * @param {Object} config - ì„¤ì • ì˜µì…˜
   * @returns {Promise<Object>} - ì—…ë¡œë“œ ê²°ê³¼
   */
  const upload = useCallback(async (url, formData, config = {}) => {
    const { 
      headers = {}, 
      onSuccess, 
      onError,
      resetOnSuccess = true 
    } = config;

    setIsUploading(true);
    setUploadProgress(0);
    setError(null);

    try {
      // Authorization í—¤ë” ìë™ ì¶”ê°€
      const token = localStorage.getItem('accessToken');
      if (token && !headers.Authorization) {
        headers.Authorization = `Bearer ${token}`;
      }

      const result = await uploadWithProgress(url, formData, headers);

      // ì„±ê³µ ì²˜ë¦¬
      const responseData = result.data || result;
      setUploadedFile(responseData);

      if (onSuccess) {
        onSuccess(responseData);
      }

      if (resetOnSuccess) {
        setTimeout(() => {
          resetState();
        }, 1000);
      }

      return responseData;
    } catch (err) {
      console.error('Upload error:', err);
      setError(err.message || 'ì—…ë¡œë“œ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');

      if (onError) {
        onError(err);
      }

      throw err;
    } finally {
      setIsUploading(false);
    }
  }, [uploadWithProgress, resetState]);

  /**
   * ì—¬ëŸ¬ íŒŒì¼ ë™ì‹œ ì—…ë¡œë“œ
   * @param {Array} uploadTasks - ì—…ë¡œë“œ ì‘ì—… ë°°ì—´ [{url, formData, config}]
   * @returns {Promise<Array>} - ì—…ë¡œë“œ ê²°ê³¼ ë°°ì—´
   */
  const uploadMultiple = useCallback(async (uploadTasks) => {
    setIsUploading(true);
    setError(null);

    try {
      const results = [];
      let completedCount = 0;

      for (const task of uploadTasks) {
        const { url, formData, config = {} } = task;

        try {
          const result = await upload(url, formData, {
            ...config,
            resetOnSuccess: false,
            onSuccess: (data) => {
              completedCount++;
              setUploadProgress(Math.round((completedCount / uploadTasks.length) * 100));
              if (config.onSuccess) {
                config.onSuccess(data);
              }
            }
          });
          results.push({ success: true, data: result });
        } catch (error) {
          results.push({ success: false, error: error.message });
        }
      }

      return results;
    } catch (err) {
      setError(err.message || 'ë‹¤ì¤‘ ì—…ë¡œë“œ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
      throw err;
    } finally {
      setIsUploading(false);
    }
  }, [upload]);

  return {
    // ìƒíƒœ
    isUploading,
    uploadProgress,
    error,
    uploadedFile,

    // í•¨ìˆ˜
    upload,
    uploadMultiple,
    resetState,

    // ìœ í‹¸ë¦¬í‹°
    setError,
    setUploadProgress
  };
}

/**
 * ì´ë¯¸ì§€ ì—…ë¡œë“œ ì „ìš© í›…
 */
export function useImageUpload(options = {}) {
  const uploadHook = useFileUpload(options);
  const WORKERS_API_URL = import.meta.env.VITE_WORKERS_API_URL || 'https://workers.languagemate.kr';

  const uploadImage = useCallback(async (file, type = 'general', metadata = {}) => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('type', type);

    if (metadata && Object.keys(metadata).length > 0) {
      formData.append('metadata', JSON.stringify(metadata));
    }

    return uploadHook.upload(`${WORKERS_API_URL}/api/v1/upload/image`, formData, options);
  }, [uploadHook, WORKERS_API_URL, options]);

  return {
    ...uploadHook,
    uploadImage
  };
}

