}

import { useState, useEffect, useRef, useCallback } from 'react';

const WORKERS_API_URL = import.meta.env.VITE_WORKERS_API_URL || 'https://workers.languagemate.kr';

// ICEì„œë²„ ì„¤ì •
const ICE_SERVERS = [
  { urls: 'stun:stun.l.google.com:19302' },
  { urls: 'stun:stun1.l.google.com:19302' },
  // TURN ì„œë²„ (í•„ìš”ì‹œ ì¶”ê°€)
];

export default function useWebRTC(roomId, userId) {
  // ìƒíƒœ ê´€ë¦¬
  const [connectionState, setConnectionState] = useState('new'); // new, connecting, connected, disconnected, failed
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState(new Map());
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    bitrate: 0,
    packetLoss: 0,
    latency: 0,
    quality: 'good' // good, fair, poor
  });

  // Refs
  const wsRef = useRef(null);
  const peerConnectionsRef = useRef(new Map());
  const localStreamRef = useRef(null);
  const statsIntervalRef = useRef(null);

  // ë¯¸ë””ì–´ ìŠ¤íŠ¸ë¦¼ íšë“
  const getUserMedia = useCallback(async (constraints = {}) => {
    try {
      const defaultConstraints = {
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        },
        video: constraints.video !== false ? {
          width: { ideal: 1280, max: 1920 },
          height: { ideal: 720, max: 1080 },
          frameRate: { ideal: 24, max: 30 }
        } : false
      };

      const stream = await navigator.mediaDevices.getUserMedia({
        ...defaultConstraints,
        ...constraints
      });

      localStreamRef.current = stream;
      setLocalStream(stream);
      setError(null);

      return stream;
    } catch (err) {
      console.error('Failed to get user media:', err);
      setError(err.message);

      // ë¹„ë””ì˜¤ ì‹¤íŒ¨ì‹œ ì˜¤ë””ì˜¤ë§Œ ì‹œë„
      if (err.name === 'NotFoundError' && constraints.video !== false) {
        return getUserMedia({ video: false });
      }

      throw err;
    }
  }, []);

  // WebSocket ì—°ê²°
  const connectWebSocket = useCallback(() => {
    if (!roomId || !userId) return;

    const wsUrl = `${WORKERS_API_URL.replace('https', 'wss')}/api/room/${roomId}/ws`;
    const ws = new WebSocket(wsUrl);

    ws.onopen = () => {
      console.log('WebSocket connected');
      setConnectionState('connecting');

      // ì‚¬ìš©ì ì •ë³´ ì „ì†¡
      ws.send(JSON.stringify({
        type: 'join',
        userId,
        userName: userId // ì‹¤ì œë¡œëŠ” ì‚¬ìš©ì ì´ë¦„ ì „ë‹¬
      }));
    };

    ws.onmessage = async (event) => {
      const message = JSON.parse(event.data);

      switch (message.type) {
        case 'connected':
          console.log('Joined room:', message.roomData);
          break;

        case 'participant-joined':
          console.log('New participant:', message.participant);
          await createPeerConnection(message.participant.id, true);
          break;

        case 'participant-left':
          console.log('Participant left:', message.participantId);
          closePeerConnection(message.participantId);
          break;

        case 'offer':
          await handleOffer(message.from, message.data);
          break;

        case 'answer':
          await handleAnswer(message.from, message.data);
          break;

        case 'ice-candidate':
          await handleIceCandidate(message.from, message.data);
          break;

        case 'participant-updated':
          handleParticipantUpdate(message.participant);
          break;
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      setError('ì—°ê²° ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤');
    };

    ws.onclose = () => {
      console.log('WebSocket disconnected');
      setConnectionState('disconnected');

      // ì¬ì—°ê²° ì‹œë„
      setTimeout(() => {
        if (wsRef.current === ws) {
          connectWebSocket();
        }
      }, 3000);
    };

    wsRef.current = ws;
    return ws;
  }, [roomId, userId]);

  // Peer Connection ìƒì„±
  const createPeerConnection = useCallback(async (peerId, createOffer = false) => {
    console.log('Creating peer connection for:', peerId);

    const pc = new RTCPeerConnection({ iceServers: ICE_SERVERS });
    peerConnectionsRef.current.set(peerId, pc);

    // ë¡œì»¬ ìŠ¤íŠ¸ë¦¼ ì¶”ê°€
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach(track => {
        pc.addTrack(track, localStreamRef.current);
      });
    }

    // ICE í›„ë³´ ì²˜ë¦¬
    pc.onicecandidate = (event) => {
      if (event.candidate && wsRef.current?.readyState === WebSocket.OPEN) {
        wsRef.current.send(JSON.stringify({
          type: 'ice-candidate',
          data: {
            to: peerId,
            signal: event.candidate
          }
        }));
      }
    };

    // ì›ê²© ìŠ¤íŠ¸ë¦¼ ì²˜ë¦¬
    pc.ontrack = (event) => {
      console.log('Received remote track from:', peerId);
      setRemoteStreams(prev => new Map(prev).set(peerId, event.streams[0]));
    };

    // ì—°ê²° ìƒíƒœ ëª¨ë‹ˆí„°ë§
    pc.onconnectionstatechange = () => {
      console.log(`Connection state for ${peerId}:`, pc.connectionState);

      if (pc.connectionState === 'connected') {
        setConnectionState('connected');
        startStatsMonitoring(pc, peerId);
      } else if (pc.connectionState === 'failed') {
        handleConnectionFailure(peerId);
      }
    };

    // Offer ìƒì„± ë° ì „ì†¡
    if (createOffer) {
      try {
        const offer = await pc.createOffer();
        await pc.setLocalDescription(offer);

        if (wsRef.current?.readyState === WebSocket.OPEN) {
          wsRef.current.send(JSON.stringify({
            type: 'offer',
            data: {
              to: peerId,
              signal: offer
            }
          }));
        }
      } catch (err) {
        console.error('Failed to create offer:', err);
        setError('ì—°ê²° ìƒì„± ì‹¤íŒ¨');
      }
    }

    return pc;
  }, []);

  // Offer ì²˜ë¦¬
  const handleOffer = useCallback(async (fromId, offer) => {
    console.log('Handling offer from:', fromId);

    try {
      const pc = await createPeerConnection(fromId, false);
      await pc.setRemoteDescription(new RTCSessionDescription(offer));

      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);

      if (wsRef.current?.readyState === WebSocket.OPEN) {
        wsRef.current.send(JSON.stringify({
          type: 'answer',
          data: {
            to: fromId,
            signal: answer
          }
        }));
      }
    } catch (err) {
      console.error('Failed to handle offer:', err);
      setError('ì—°ê²° ìˆ˜ë½ ì‹¤íŒ¨');
    }
  }, [createPeerConnection]);

  // Answer ì²˜ë¦¬
  const handleAnswer = useCallback(async (fromId, answer) => {
    console.log('Handling answer from:', fromId);

    const pc = peerConnectionsRef.current.get(fromId);
    if (pc) {
      try {
        await pc.setRemoteDescription(new RTCSessionDescription(answer));
      } catch (err) {
        console.error('Failed to handle answer:', err);
        setError('ì—°ê²° ì„¤ì • ì‹¤íŒ¨');
      }
    }
  }, []);

  // ICE Candidate ì²˜ë¦¬
  const handleIceCandidate = useCallback(async (fromId, candidate) => {
    const pc = peerConnectionsRef.current.get(fromId);
    if (pc && candidate) {
      try {
        await pc.addIceCandidate(new RTCIceCandidate(candidate));
      } catch (err) {
        console.error('Failed to add ICE candidate:', err);
      }
    }
  }, []);

  // ì°¸ê°€ì ì—…ë°ì´íŠ¸ ì²˜ë¦¬
  const handleParticipantUpdate = useCallback((participant) => {
    console.log('Participant updated:', participant);
    // UI ì—…ë°ì´íŠ¸ ë“± í•„ìš”í•œ ì²˜ë¦¬
  }, []);

  // ì—°ê²° ì‹¤íŒ¨ ì²˜ë¦¬
  const handleConnectionFailure = useCallback((peerId) => {
    console.log('Connection failed for:', peerId);

    // ICE ì¬ì‹œì‘ ì‹œë„
    const pc = peerConnectionsRef.current.get(peerId);
    if (pc) {
      pc.restartIce();
    }
  }, []);

  // Peer Connection ì¢…ë£Œ
  const closePeerConnection = useCallback((peerId) => {
    const pc = peerConnectionsRef.current.get(peerId);
    if (pc) {
      pc.close();
      peerConnectionsRef.current.delete(peerId);
    }

    setRemoteStreams(prev => {
      const newStreams = new Map(prev);
      newStreams.delete(peerId);
      return newStreams;
    });
  }, []);

  // í†µê³„ ëª¨ë‹ˆí„°ë§
  const startStatsMonitoring = useCallback((pc, peerId) => {
    const interval = setInterval(async () => {
      if (pc.connectionState !== 'connected') {
        clearInterval(interval);
        return;
      }

      try {
        const stats = await pc.getStats();
        let totalBitrate = 0;
        let totalPacketLoss = 0;
        let totalPackets = 0;
        let rtts = [];

        stats.forEach(report => {
          if (report.type === 'outbound-rtp' && report.kind === 'video') {
            totalBitrate += report.bytesSent * 8 / report.timestamp;
            totalPacketLoss += report.packetsLost || 0;
            totalPackets += report.packetsSent || 0;
          }

          if (report.type === 'candidate-pair' && report.state === 'succeeded') {
            if (report.currentRoundTripTime) {
              rtts.push(report.currentRoundTripTime * 1000); // ms
            }
          }
        });

        const packetLossRate = totalPackets > 0 ? totalPacketLoss / totalPackets : 0;
        const avgLatency = rtts.length > 0 ? rtts.reduce((a, b) => a + b) / rtts.length : 0;

        // í’ˆì§ˆ íŒë‹¨
        let quality = 'good';
        if (packetLossRate > 0.05 || avgLatency > 150) {
          quality = 'fair';
        }
        if (packetLossRate > 0.1 || avgLatency > 300) {
          quality = 'poor';
        }

        setStats({
          bitrate: Math.round(totalBitrate / 1000), // kbps
          packetLoss: Math.round(packetLossRate * 100), // %
          latency: Math.round(avgLatency), // ms
          quality
        });
      } catch (err) {
        console.error('Failed to get stats:', err);
      }
    }, 2000);

    return interval;
  }, []);

  // ì˜¤ë””ì˜¤ í† ê¸€
  const toggleAudio = useCallback(() => {
    if (localStreamRef.current) {
      const audioTracks = localStreamRef.current.getAudioTracks();
      audioTracks.forEach(track => {
        track.enabled = !track.enabled;
      });

      const newState = audioTracks[0]?.enabled ?? false;
      setIsAudioEnabled(newState);

      // ì„œë²„ì— ìƒíƒœ ì „ì†¡
      if (wsRef.current?.readyState === WebSocket.OPEN) {
        wsRef.current.send(JSON.stringify({
          type: 'toggle-audio',
          data: { enabled: newState }
        }));
      }
    }
  }, []);

  // ë¹„ë””ì˜¤ í† ê¸€
  const toggleVideo = useCallback(() => {
    if (localStreamRef.current) {
      const videoTracks = localStreamRef.current.getVideoTracks();
      videoTracks.forEach(track => {
        track.enabled = !track.enabled;
      });

      const newState = videoTracks[0]?.enabled ?? false;
      setIsVideoEnabled(newState);

      // ì„œë²„ì— ìƒíƒœ ì „ì†¡
      if (wsRef.current?.readyState === WebSocket.OPEN) {
        wsRef.current.send(JSON.stringify({
          type: 'toggle-video',
          data: { enabled: newState }
        }));
      }
    }
  }, []);

  // ì—°ê²° ì¢…ë£Œ
  const disconnect = useCallback(() => {
    // ëª¨ë“  Peer Connection ì¢…ë£Œ
    peerConnectionsRef.current.forEach((pc, peerId) => {
      closePeerConnection(peerId);
    });

    // ë¡œì»¬ ìŠ¤íŠ¸ë¦¼ ì¢…ë£Œ
    if (localStreamRef.current) {
      localStreamRef.current.getTracks().forEach(track => track.stop());
      localStreamRef.current = null;
      setLocalStream(null);
    }

    // WebSocket ì¢…ë£Œ
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }

    // í†µê³„ ëª¨ë‹ˆí„°ë§ ì¤‘ì§€
    if (statsIntervalRef.current) {
      clearInterval(statsIntervalRef.current);
    }

    setConnectionState('disconnected');
    setRemoteStreams(new Map());
  }, [closePeerConnection]);

  // ì´ˆê¸° ì—°ê²° ì„¤ì •
  useEffect(() => {
    if (!roomId || !userId) return;

    const init = async () => {
      try {
        // ë¯¸ë””ì–´ ìŠ¤íŠ¸ë¦¼ íšë“
        await getUserMedia();

        // WebSocket ì—°ê²°
        connectWebSocket();
      } catch (err) {
        console.error('Failed to initialize:', err);
        setError('ì´ˆê¸°í™” ì‹¤íŒ¨: ' + err.message);
      }
    };

    init();

    // í´ë¦°ì—…
    return () => {
      disconnect();
    };
  }, [roomId, userId]); // getUserMedia, connectWebSocket, disconnectëŠ” ì˜ì¡´ì„±ì—ì„œ ì œì™¸

  return {
    // ìƒíƒœ
    connectionState,
    localStream,
    remoteStreams,
    isAudioEnabled,
    isVideoEnabled,
    error,
    stats,

    // ì•¡ì…˜
    toggleAudio,
    toggleVideo,
    disconnect,
    getUserMedia
  };
}

import { useState, useRef, useCallback, useEffect } from 'react';
import { log } from '../utils/logger';

const WS_URL = import.meta.env.VITE_WORKERS_WS_URL || 'wss://workers.languagemate.kr/ws';

export function useWebSocketTranscription({
  language = 'auto',
  targetLanguage = 'ko',
  enableTranslation = true,
  onTranscript,
  onError,
  reconnectAttempts = 3,
  reconnectDelay = 2000
} = {}) {
  // ìƒíƒœ ê´€ë¦¬
  const [isConnected, setIsConnected] = useState(false);
  const [isTranscribing, setIsTranscribing] = useState(false);
  const [currentTranscript, setCurrentTranscript] = useState(null);
  const [error, setError] = useState(null);
  const [connectionQuality, setConnectionQuality] = useState('good');

  // ë ˆí¼ëŸ°ìŠ¤ ê´€ë¦¬
  const wsRef = useRef(null);
  const mediaStreamRef = useRef(null);
  const audioContextRef = useRef(null);
  const processorRef = useRef(null);
  const reconnectTimeoutRef = useRef(null);
  const reconnectCountRef = useRef(0);
  const lastTranscriptTimeRef = useRef(Date.now());

  // WebSocket ì—°ê²°
  const connectWebSocket = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      return Promise.resolve();
    }

    return new Promise((resolve, reject) => {
      try {
        const wsUrl = `${WS_URL}/api/transcribe/stream`;
        const ws = new WebSocket(wsUrl);

        ws.onopen = () => {
          log.info('WebSocket ì „ì‚¬ ì—°ê²° ì„±ê³µ', { wsUrl }, 'WS_TRANSCRIPTION');
          setIsConnected(true);
          setError(null);
          reconnectCountRef.current = 0;

          // ì´ˆê¸° ì„¤ì • ì „ì†¡
          ws.send(JSON.stringify({
            type: 'config',
            config: {
              language,
              targetLanguage,
              enableTranslation,
              model: 'whisper-large-v3-turbo',
              responseFormat: 'verbose_json',
              temperature: 0.1,
              vadFilter: true,
              initialPrompt: 'This is a conversation between language learners.'
            }
          }));

          resolve(ws);
        };

        ws.onmessage = (event) => {
          try {
            const data = JSON.parse(event.data);
            handleWebSocketMessage(data);
          } catch (err) {
            log.error('WebSocket ë©”ì‹œì§€ íŒŒì‹± ì˜¤ë¥˜', err, 'WS_TRANSCRIPTION');
          }
        };

        ws.onerror = (error) => {
          log.error('WebSocket ì—°ê²° ì˜¤ë¥˜', error, 'WS_TRANSCRIPTION');
          setError('ì‹¤ì‹œê°„ ì „ì‚¬ ì—°ê²°ì— ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
          setConnectionQuality('poor');
          reject(error);
        };

        ws.onclose = (event) => {
          log.info('WebSocket ì—°ê²° ì¢…ë£Œ', { code: event.code, reason: event.reason }, 'WS_TRANSCRIPTION');
          setIsConnected(false);

          // ìë™ ì¬ì—°ê²° ì‹œë„
          if (isTranscribing && reconnectCountRef.current < reconnectAttempts) {
            scheduleReconnect();
          }
        };

        wsRef.current = ws;

      } catch (err) {
        log.error('WebSocket ì—°ê²° ì‹¤íŒ¨', err, 'WS_TRANSCRIPTION');
        setError('ì‹¤ì‹œê°„ ì „ì‚¬ ì„œë¹„ìŠ¤ì— ì—°ê²°í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
        reject(err);
      }
    });
  }, [language, targetLanguage, enableTranslation, isTranscribing, reconnectAttempts]);

  // WebSocket ë©”ì‹œì§€ ì²˜ë¦¬
  const handleWebSocketMessage = useCallback((data) => {
    switch (data.type) {
      case 'transcription': {
        const transcript = {
          id: `transcript-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
          text: data.text,
          language: data.language || language,
          confidence: data.confidence || 1.0,
          isFinal: data.is_final || false,
          timestamp: new Date().toISOString(),
          duration: data.duration || 0,
          words: data.words || []
        };

        if (data.translations && enableTranslation) {
          transcript.translations = data.translations;
        }

        setCurrentTranscript(transcript);
        lastTranscriptTimeRef.current = Date.now();

        if (onTranscript) {
          onTranscript(transcript);
        }

        // ì—°ê²° í’ˆì§ˆ ì—…ë°ì´íŠ¸
        setConnectionQuality('good');
        break;
      }

      case 'translation':
        // ë²ˆì—­ ê²°ê³¼ ì²˜ë¦¬
        if (onTranscript) {
          onTranscript({
            id: `translation-${Date.now()}`,
            text: data.translatedText,
            originalText: data.originalText,
            sourceLanguage: data.sourceLanguage,
            targetLanguage: data.targetLanguage,
            timestamp: new Date().toISOString(),
            isTranslation: true
          });
        }
        break;

      case 'error': {
        const errorMessage = data.message || 'ì „ì‚¬ ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.';
        setError(errorMessage);
        setConnectionQuality('poor');
        if (onError) {
          onError(new Error(errorMessage));
        }
        break;
      }

      case 'status':
        log.info('WebSocket ìƒíƒœ ì—…ë°ì´íŠ¸', data, 'WS_TRANSCRIPTION');
        break;

      default:
        log.warn('ì•Œ ìˆ˜ ì—†ëŠ” ë©”ì‹œì§€ íƒ€ì…', data, 'WS_TRANSCRIPTION');
    }
  }, [language, enableTranslation, onTranscript, onError]);

  // ì¬ì—°ê²° ìŠ¤ì¼€ì¤„ë§
  const scheduleReconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
    }

    reconnectCountRef.current++;
    const delay = reconnectDelay * Math.pow(2, reconnectCountRef.current - 1);

    log.info(`WebSocket ì¬ì—°ê²° ì‹œë„ ì˜ˆì •`, { 
      attempt: reconnectCountRef.current, 
      delay,
      maxAttempts: reconnectAttempts 
    }, 'WS_TRANSCRIPTION');

    reconnectTimeoutRef.current = setTimeout(async () => {
      try {
        await connectWebSocket();
        if (mediaStreamRef.current) {
          await startAudioCapture(mediaStreamRef.current);
        }
      } catch (err) {
        log.error('ì¬ì—°ê²° ì‹¤íŒ¨', err, 'WS_TRANSCRIPTION');
        if (reconnectCountRef.current >= reconnectAttempts) {
          setError(`ì—°ê²°ì„ ${reconnectAttempts}íšŒ ì‹œë„í–ˆì§€ë§Œ ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.`);
        }
      }
    }, delay);
  }, [connectWebSocket, reconnectDelay, reconnectAttempts]);

  // ì˜¤ë””ì˜¤ ìº¡ì²˜ ì‹œì‘
  const startAudioCapture = useCallback(async (stream) => {
    try {
      // ì˜¤ë””ì˜¤ ì»¨í…ìŠ¤íŠ¸ ìƒì„±
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)({
        sampleRate: 16000
      });

      // ì†ŒìŠ¤ ìƒì„±
      const source = audioContextRef.current.createMediaStreamSource(stream);

      // ScriptProcessorNode ìƒì„± (AudioWorkletì´ ì§€ì›ë˜ì§€ ì•ŠëŠ” ê²½ìš°)
      if (audioContextRef.current.audioWorklet) {
        try {
          await audioContextRef.current.audioWorklet.addModule('/audioProcessor.js');
          processorRef.current = new AudioWorkletNode(audioContextRef.current, 'pcm-processor');
        } catch {
          // AudioWorklet ì‹¤íŒ¨ ì‹œ ScriptProcessorNode ì‚¬ìš©
          processorRef.current = audioContextRef.current.createScriptProcessor(4096, 1, 1);
        }
      } else {
        processorRef.current = audioContextRef.current.createScriptProcessor(4096, 1, 1);
      }

      // ì˜¤ë””ì˜¤ ë°ì´í„° ì²˜ë¦¬
      if (processorRef.current.port) {
        // AudioWorkletNode
        processorRef.current.port.onmessage = (event) => {
          if (wsRef.current?.readyState === WebSocket.OPEN) {
            wsRef.current.send(event.data.audioData);
          }
        };
      } else {
        // ScriptProcessorNode
        processorRef.current.onaudioprocess = (event) => {
          if (wsRef.current?.readyState === WebSocket.OPEN) {
            const inputBuffer = event.inputBuffer;
            const channelData = inputBuffer.getChannelData(0);
            const audioData = new Float32Array(channelData);
            wsRef.current.send(audioData.buffer);
          }
        };
      }

      // ì—°ê²°
      source.connect(processorRef.current);
      processorRef.current.connect(audioContextRef.current.destination);

      log.info('ì˜¤ë””ì˜¤ ìº¡ì²˜ ì‹œì‘', { sampleRate: audioContextRef.current.sampleRate }, 'WS_TRANSCRIPTION');

    } catch (err) {
      log.error('ì˜¤ë””ì˜¤ ìº¡ì²˜ ì´ˆê¸°í™” ì‹¤íŒ¨', err, 'WS_TRANSCRIPTION');
      setError('ë§ˆì´í¬ ì ‘ê·¼ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
      throw err;
    }
  }, []);

  // ì „ì‚¬ ì‹œì‘
  const startTranscription = useCallback(async (stream) => {
    if (!stream) {
      setError('ì˜¤ë””ì˜¤ ìŠ¤íŠ¸ë¦¼ì´ ì œê³µë˜ì§€ ì•Šì•˜ìŠµë‹ˆë‹¤.');
      return;
    }

    try {
      setError(null);
      setIsTranscribing(true);
      mediaStreamRef.current = stream;

      // WebSocket ì—°ê²°
      await connectWebSocket();

      // ì˜¤ë””ì˜¤ ìº¡ì²˜ ì‹œì‘
      await startAudioCapture(stream);

      log.info('ì‹¤ì‹œê°„ ì „ì‚¬ ì‹œì‘', { language, targetLanguage, enableTranslation }, 'WS_TRANSCRIPTION');

    } catch (err) {
      log.error('ì „ì‚¬ ì‹œì‘ ì‹¤íŒ¨', err, 'WS_TRANSCRIPTION');
      setIsTranscribing(false);
      setError(err.message || 'ì „ì‚¬ë¥¼ ì‹œì‘í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
      if (onError) {
        onError(err);
      }
    }
  }, [connectWebSocket, startAudioCapture, language, targetLanguage, enableTranslation, onError]);

  // ì „ì‚¬ ì¤‘ì§€
  const stopTranscription = useCallback(() => {
    log.info('ì‹¤ì‹œê°„ ì „ì‚¬ ì¤‘ì§€', null, 'WS_TRANSCRIPTION');

    setIsTranscribing(false);

    // WebSocket ì—°ê²° ì¢…ë£Œ
    if (wsRef.current) {
      wsRef.current.close(1000, 'User requested stop');
      wsRef.current = null;
    }

    // ì˜¤ë””ì˜¤ ë¦¬ì†ŒìŠ¤ ì •ë¦¬
    if (processorRef.current) {
      processorRef.current.disconnect();
      processorRef.current = null;
    }

    if (audioContextRef.current) {
      audioContextRef.current.close();
      audioContextRef.current = null;
    }

    // ì¬ì—°ê²° íƒ€ì´ë¨¸ ì •ë¦¬
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
      reconnectTimeoutRef.current = null;
    }

    setIsConnected(false);
    setCurrentTranscript(null);
    setError(null);
    reconnectCountRef.current = 0;
  }, []);

  // ì „ì‚¬ í† ê¸€
  const toggleTranscription = useCallback(async (stream) => {
    if (isTranscribing) {
      stopTranscription();
    } else if (stream) {
      await startTranscription(stream);
    } else {
      setError('ì˜¤ë””ì˜¤ ìŠ¤íŠ¸ë¦¼ì´ í•„ìš”í•©ë‹ˆë‹¤.');
    }
  }, [isTranscribing, startTranscription, stopTranscription]);

  // ì–¸ì–´ ì„¤ì • ì—…ë°ì´íŠ¸
  const updateLanguage = useCallback((newLanguage, newTargetLanguage) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({
        type: 'config',
        config: {
          language: newLanguage,
          targetLanguage: newTargetLanguage,
          enableTranslation
        }
      }));
    }
  }, [enableTranslation]);

  // ì—°ê²° ìƒíƒœ ëª¨ë‹ˆí„°ë§
  useEffect(() => {
    if (!isTranscribing) return;

    const interval = setInterval(() => {
      const timeSinceLastTranscript = Date.now() - lastTranscriptTimeRef.current;

      // 10ì´ˆ ì´ìƒ ì‘ë‹µì´ ì—†ìœ¼ë©´ ì—°ê²° í’ˆì§ˆì„ 'poor'ë¡œ ì„¤ì •
      if (timeSinceLastTranscript > 10000) {
        setConnectionQuality('poor');
      } else if (timeSinceLastTranscript > 5000) {
        setConnectionQuality('fair');
      } else {
        setConnectionQuality('good');
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [isTranscribing]);

  // ì»´í¬ë„ŒíŠ¸ ì–¸ë§ˆìš´íŠ¸ ì‹œ ì •ë¦¬
  useEffect(() => {
    return () => {
      stopTranscription();
    };
  }, [stopTranscription]);

  return {
    // ìƒíƒœ
    isConnected,
    isTranscribing,
    currentTranscript,
    error,
    connectionQuality,

    // ë©”ì„œë“œ
    startTranscription,
    stopTranscription,
    toggleTranscription,
    updateLanguage,

    // ì—°ê²° ì •ë³´
    reconnectCount: reconnectCountRef.current,
    maxReconnectAttempts: reconnectAttempts
  };
}

