import { useState, useEffect } from 'react';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Activity, TrendingUp, AlertCircle, Clock, Users, Globe, Zap, Server } from 'lucide-react';

// ìƒ‰ìƒ íŒ”ë ˆíŠ¸ (ë””ìì¸ ì‹œìŠ¤í…œ ì¤€ìˆ˜)
const COLORS = [
  'var(--green-500)',
  'var(--red-500)',
  'var(--blue)',
  'var(--green-400)',
  'var(--warning-yellow)',
  'var(--black-300)'
];

export default function AnalyticsDashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [timeRange, setTimeRange] = useState('24h');
  const [realTimeMetrics, setRealTimeMetrics] = useState(null);

  useEffect(() => {
    fetchDashboardData();
    connectToMetricsStream();

    return () => {
      // Cleanup WebSocket connection
      if (window.metricsWs) {
        window.metricsWs.close();
      }
    };
  }, [timeRange]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/v1/analytics/dashboard`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) throw new Error('Failed to fetch dashboard data');

      const data = await response.json();
      setDashboardData(data.data);
    } catch (error) {
      console.error('Dashboard data error:', error);
    } finally {
      setLoading(false);
    }
  };

  const connectToMetricsStream = () => {
    const ws = new WebSocket(`wss://${window.location.host}/api/v1/analytics/stream`);

    ws.onopen = () => {
      console.log('Connected to metrics stream');
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.type === 'metrics_update') {
        setRealTimeMetrics(data.data);
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    window.metricsWs = ws;
