            title="Avg Response Time"
            value={`${overview?.avgDuration?.toFixed(0) || 0}ms`}
            change="-5.2%"
            color="#45B7D1"
          />
          <MetricCard
            icon={AlertCircle}
            title="Error Rate"
            value={`${((overview?.errorRate || 0) * 100).toFixed(1)}%`}
