// JWT í† í° í˜•ì‹ ê²€ì¦ í•¨ìˆ˜
const isValidJWT = (token) => {
  if (!token || typeof token !== 'string') return false;
  const parts = token.split('.');
  return parts.length === 3 && parts.every(part => part.length > 0);
};

