import React, { useState, useEffect } from 'react';
import { 
  getMyNotifications,
  getUnreadNotifications,
  markNotificationAsRead,
  markAllNotificationsAsRead,
  deleteNotification,
  getUnreadNotificationCount
} from '../../api/notifications';
import Header from '../../components/Header';
import { useAlert } from '../../hooks/useAlert';
import { Bell, Check, Trash2, MessageCircle, Users, Calendar, Award, AlertCircle } from 'lucide-react';

export default function NotificationPage() {
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('ALL'); // ALL, UNREAD
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const { showError, showSuccess } = useAlert();

  useEffect(() => {
    loadNotifications();
    loadUnreadCount();
  }, [filter]);

  const loadNotifications = async () => {
    try {
      setLoading(true);
      let data;

      if (filter === 'UNREAD') {
        const response = await getUnreadNotifications();
        data = response.data || [];
        setNotifications(data);
        setHasMore(false); // ì½ì§€ ì•Šì€ ì•Œë¦¼ì€ í˜ì´ì§• ì—†ì´ ì „ì²´ ë¡œë“œ
      } else {
        const response = await getMyNotifications(page, 20);
        data = response.data || response;

        if (data.content) {
          setNotifications(prev => page === 0 ? data.content : [...prev, ...data.content]);
          setHasMore(!data.last);
        } else if (Array.isArray(data)) {
          setNotifications(data);
          setHasMore(false);
        }
      }
    } catch (error) {
      showError('ì•Œë¦¼ì„ ë¶ˆëŸ¬ì˜¤ëŠ”ë° ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const loadUnreadCount = async () => {
    try {
      const response = await getUnreadNotificationCount();
      setUnreadCount(response.data || 0);
    } catch (error) {
      console.error('Failed to load unread count:', error);
    }
  };

  const handleMarkAsRead = async (notificationId) => {
    try {
      await markNotificationAsRead(notificationId);
      setNotifications(prev => 
        prev.map(n => 
          n.id === notificationId ? { ...n, isRead: true } : n
        )
      );
      setUnreadCount(prev => Math.max(0, prev - 1));
    } catch (error) {
      showError('ì½ìŒ ì²˜ë¦¬ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.');
      console.error(error);
    }
  };

  const handleMarkAllAsRead = async () => {
    try {
      await markAllNotificationsAsRead();
      setNotifications(prev => prev.map(n => ({ ...n, isRead: true })));
      setUnreadCount(0);
      showSuccess('ëª¨ë“  ì•Œë¦¼ì„ ì½ìŒ ì²˜ë¦¬í–ˆìŠµë‹ˆë‹¤.');
