import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ChevronLeft, 
  Filter, 
  MoreVertical, 
  Check, 
  CheckCheck, 
  Trash2, 
  Search,
  Bell,
  MessageSquare,
  Users,
  Calendar,
  Award,
  AlertCircle,
  Info,
  RefreshCw
} from 'lucide-react';
import useNotificationStore from '../../store/notificationStore';
import CommonButton from '../../components/CommonButton';

const NotificationList = () => {
  const navigate = useNavigate();
  const {
    notifications,
    unreadCount,
    loading,
    error,
    filter,
    pagination,
    loadNotifications,
    loadUnreadCount,
    markAsRead,
    markAllAsRead,
    deleteNotification,
    deleteSelectedNotifications,
    setFilter,
    clearFilter,
    clearError,
    getGroupedNotifications,
    searchNotifications
  } = useNotificationStore();

  const [selectedNotifications, setSelectedNotifications] = useState([]);
  const [showFilters, setShowFilters] = useState(false);
  const [showActions, setShowActions] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isSelectionMode, setIsSelectionMode] = useState(false);

  useEffect(() => {
    loadNotifications(1, 20, true);
    loadUnreadCount();
  }, [filter]);

  useEffect(() => {
    if (error) {
      const timer = setTimeout(() => {
        clearError();
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [error, clearError]);

  const handleLoadMore = useCallback(() => {
    if (pagination.hasNext && !loading) {
      loadNotifications(pagination.page + 1, 20, false);
    }
  }, [pagination.hasNext, pagination.page, loading, loadNotifications]);

  const handleNotificationClick = async (notification) => {
    if (!notification.isRead) {
      await markAsRead(notification.id);
    }

    if (notification.clickUrl) {
      navigate(notification.clickUrl);
    } else {
      // ê¸°ë³¸ ì•¡ì…˜ ì²˜ë¦¬
      const baseType = notification.category || notification.type;
      switch (baseType) {
        case 'chat':
          navigate('/chat');
          break;
        case 'matching':
        case 'match_request':
        case 'match_accepted':
          navigate('/matching');
          break;
        case 'session':
        case 'session_reminder':
        case 'session_invitation':
          navigate('/main');
          break;
        case 'achievement':
        case 'level_test_result':
          navigate('/profile');
          break;
        default:
          break;
      }
    }
  };

  const handleMarkAsRead = async (notificationId, event) => {
    event.stopPropagation();
    await markAsRead(notificationId);
  };

  const handleDeleteNotification = async (notificationId, event) => {
    event.stopPropagation();
    if (window.confirm('ì´ ì•Œë¦¼ì„ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?')) {
      await deleteNotification(notificationId);
    }
  };

  const handleSelectNotification = (notificationId) => {
    setSelectedNotifications(prev => {
      if (prev.includes(notificationId)) {
        return prev.filter(id => id !== notificationId);
      } else {
        return [...prev, notificationId];
      }
    });
  };

  const handleSelectAll = () => {
    if (selectedNotifications.length === notifications.length) {
      setSelectedNotifications([]);
    } else {
      setSelectedNotifications(notifications.map(n => n.id));
    }
  };

  const handleBulkAction = async (action) => {
    if (selectedNotifications.length === 0) return;

    switch (action) {
      case 'markAsRead':
        for (const id of selectedNotifications) {
          await markAsRead(id);
        }
        break;
      case 'delete':
        if (window.confirm(`ì„ íƒëœ ${selectedNotifications.length}ê°œì˜ ì•Œë¦¼ì„ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?`)) {
          await deleteSelectedNotifications(selectedNotifications);
        }
        break;
    }

    setSelectedNotifications([]);
    setIsSelectionMode(false);
  };

  const getTypeIcon = (type) => {
    if (!type) return <Bell className="w-5 h-5" />;
    const normalized = type.toLowerCase();

