        // ìš”ì¼ í•´ì œ ì‹œ í•´ë‹¹ ìš”ì¼ì˜ ì‹œê°„ë„ ì œê±°
        const newSelectedTimes = { ...selectedTimes };
        delete newSelectedTimes[dayId];
        setSelectedTimes(newSelectedTimes);
        return prev.filter(id => id !== dayId);
      } else {
        return [...prev, dayId];
      }
    });
  };

  const handleTimeSelect = (dayId, time) => {
    setSelectedTimes(prev => ({
      ...prev,
      [dayId]: time
    }));
  };

  const handleNext = async () => {
    if (selectedDays.length === 0) {
      showError("ìµœì†Œ í•˜ë‚˜ì˜ ìš”ì¼ì„ ì„ íƒí•´ì£¼ì„¸ìš”.");
      return;
    }

    // ëª¨ë“  ì„ íƒëœ ìš”ì¼ì— ì‹œê°„ì´ ì„¤ì •ë˜ì—ˆëŠ”ì§€ í™•ì¸
    const hasAllTimes = selectedDays.every(day => selectedTimes[day]);
    if (!hasAllTimes) {
      showError("ì„ íƒëœ ëª¨ë“  ìš”ì¼ì— ì‹œê°„ì„ ì„¤ì •í•´ì£¼ì„¸ìš”.");
      return;
    }

    try {
      const requestData = {
        schedules: selectedDays.map(dayId => ({
          dayOfWeek: dayId,
          classTime: selectedTimes[dayId]
        }))
      };

      await api.post("/onboarding/schedule", requestData);
      console.log("ìŠ¤ì¼€ì¤„ ì„ íƒ ë°ì´í„° ì „ì†¡ ì„±ê³µ");
      navigate("/onboarding-schedule/complete");
    } catch (error) {
      console.error("ìŠ¤ì¼€ì¤„ ì„ íƒ ë°ì´í„° ì „ì†¡ ì‹¤íŒ¨:", error);
      showError("ë°ì´í„° ì „ì†¡ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤. ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.");
    }
  };

  const getDayStyle = (dayId) => {
    const isSelected = selectedDays.includes(dayId);

    return {
      backgroundColor: isSelected ? "#e6f9f1" : "#ffffff",
      fontWeight: isSelected ? "700" : "500"
    };
  };

  // ì‹œê°„ ì„ íƒ ë“œë¡­ë°•ìŠ¤ìš© ìŠ¤íƒ€ì¼ (border ì œê±°)
  const timeSelectStyles = {
