  // Cleanup function
  const cleanup = async () => {
    log.info('ìŒì„± ì„¸ì…˜ ì •ë¦¬ ì‹œì‘', null, 'AUDIO_SESSION');

    // Stop timers and monitoring
    stopCallTimer();
