export default function SessionCreate() {
  const navigate = useNavigate();
  const [isCreating, setIsCreating] = useState(false);
  const [createdRoom, setCreatedRoom] = useState(null);
  const [error, setError] = useState('');
  const [showAdvanced, setShowAdvanced] = useState(false);

  // ì„¸ì…˜ ì„¤ì •
  const [sessionConfig, setSessionConfig] = useState({
    roomType: 'audio', // 'audio' | 'video'
    maxParticipants: 2,
    title: '',
    description: '',
    language: 'ko', // ê¸°ë³¸ ì–¸ì–´
    isPrivate: false,
    allowRecording: true,
    autoTranscription: false
  });

  // ë§¤ì²´ ê¶Œí•œ í™•ì¸
  const [mediaPermissions, setMediaPermissions] = useState({
    audio: null,
    video: null,
    checked: false
  });

  useEffect(() => {
    checkMediaPermissions();
  }, []);

  // ë¯¸ë””ì–´ ê¶Œí•œ í™•ì¸
  const checkMediaPermissions = async () => {
    try {
      const permissions = await navigator.permissions.query({ name: 'microphone' });
      const audioAllowed = permissions.state === 'granted';

      let videoAllowed = false;
      try {
        const cameraPermissions = await navigator.permissions.query({ name: 'camera' });
        videoAllowed = cameraPermissions.state === 'granted';
      } catch (err) {
        // ì¹´ë©”ë¼ ê¶Œí•œ í™•ì¸ ì‹¤íŒ¨ ì‹œ getUserMediaë¡œ í…ŒìŠ¤íŠ¸
        try {
          const stream = await navigator.mediaDevices.getUserMedia({ video: true });
          stream.getTracks().forEach(track => track.stop());
          videoAllowed = true;
        } catch {
          videoAllowed = false;
        }
      }

      setMediaPermissions({
        audio: audioAllowed,
        video: videoAllowed,
        checked: true
      });
    } catch (err) {
      console.error('Failed to check media permissions:', err);
      setMediaPermissions({
        audio: false,
        video: false,
        checked: true
      });
    }
  };

  // ê¶Œí•œ ìš”ì²­
  const requestPermissions = async () => {
    try {
      const constraints = {
        audio: true,
        video: sessionConfig.roomType === 'video'
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);

      // ì¦‰ì‹œ ìŠ¤íŠ¸ë¦¼ ì •ë¦¬
      stream.getTracks().forEach(track => track.stop());

      // ê¶Œí•œ ìƒíƒœ ì—…ë°ì´íŠ¸
      await checkMediaPermissions();

      return true;
    } catch (err) {
      console.error('Permission request failed:', err);
      setError('ë¯¸ë””ì–´ ê¶Œí•œì´ í•„ìš”í•©ë‹ˆë‹¤. ë¸Œë¼ìš°ì € ì„¤ì •ì—ì„œ ë§ˆì´í¬/ì¹´ë©”ë¼ ì ‘ê·¼ì„ í—ˆìš©í•´ì£¼ì„¸ìš”.');
