// WebP ì§€ì› ì²´í¬
export const supportsWebP = () => {
  if (typeof window === 'undefined') return false;

  const canvas = document.createElement('canvas');
  canvas.width = 1;
  canvas.height = 1;

  return canvas.toDataURL('image/webp').indexOf('image/webp') === 0;
};

// ì´ë¯¸ì§€ URL ìµœì í™”
export const getOptimizedImageUrl = (url, options = {}) => {
  if (!url || !url.startsWith('http')) return url;

  const { width, height, quality = 85, format = 'auto' } = options;

  // Cloudflare Images ë³€í™˜ API ì‚¬ìš©
  const params = new URLSearchParams({
    url,
    ...(width && { w: width }),
    ...(height && { h: height }),
    q: quality,
    f: format
  });

  return `/api/images/transform?${params}`;
};

// ë°˜ì‘í˜• ì´ë¯¸ì§€ ì†ŒìŠ¤ ìƒì„±
export const generateResponsiveSources = (baseUrl, alt) => {
  const sizes = [
    { width: 320, media: '(max-width: 640px)' },
    { width: 640, media: '(max-width: 768px)' },
    { width: 768, media: '(max-width: 1024px)' },
    { width: 1024, media: '(max-width: 1280px)' },
    { width: 1280, media: '(min-width: 1281px)' }
  ];

  return sizes.map(({ width, media }) => ({
    srcSet: getOptimizedImageUrl(baseUrl, { width }),
    media,
    type: 'image/webp'
  }));
};

// ë¸”ëŸ¬ ë°ì´í„° URL ìƒì„± (ì‘ì€ ë¯¸ë¦¬ë³´ê¸° ì´ë¯¸ì§€)
export const generateBlurDataURL = async (imageUrl) => {
  try {
    const response = await fetch(getOptimizedImageUrl(imageUrl, { 
      width: 40, 
      quality: 20 
    }));
    const blob = await response.blob();

    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.readAsDataURL(blob);
    });
  } catch (error) {
    console.error('Error generating blur data URL:', error);
    return null;
  }
};

// ì´ë¯¸ì§€ í”„ë¦¬ë¡œë“œ
export const preloadImage = (src) => {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.onload = resolve;
    img.onerror = reject;
    img.src = src;
  });
};

// ì—¬ëŸ¬ ì´ë¯¸ì§€ í”„ë¦¬ë¡œë“œ
export const preloadImages = (srcArray) => {
  return Promise.all(srcArray.map(src => preloadImage(src)));
};

import { lazy } from 'react';

/**
 * ì§€ì—° ë¡œë”© ì»´í¬ë„ŒíŠ¸ ë˜í¼
 * @param {Function} importFn - dynamic import í•¨ìˆ˜
 * @param {Object} options - ì˜µì…˜
 * @param {number} options.delay - ìµœì†Œ ë¡œë”© ì§€ì—° ì‹œê°„ (ms)
 * @param {Function} options.fallback - ì—ëŸ¬ ë°œìƒì‹œ í´ë°± ì»´í¬ë„ŒíŠ¸
 */
export const createLazyComponent = (importFn, { delay = 0, fallback = null } = {}) => {
  const LazyComponent = lazy(() => {
    return Promise.all([
      importFn(),
      // ìµœì†Œ ì§€ì—° ì‹œê°„ ë³´ì¥ (ë¡œë”© ìŠ¤í”¼ë„ˆê°€ ë„ˆë¬´ ë¹¨ë¦¬ ì‚¬ë¼ì§€ëŠ” ê²ƒ ë°©ì§€)
      delay > 0 ? new Promise(resolve => setTimeout(resolve, delay)) : Promise.resolve()
    ]).then(([moduleExports]) => moduleExports);
  });

  // ì—ëŸ¬ ê²½ê³„ê°€ ìˆëŠ” ê²½ìš° í´ë°± ì»´í¬ë„ŒíŠ¸ ì ìš©
  if (fallback) {
    LazyComponent.fallback = fallback;
  }

  return LazyComponent;
};

