/**
 * API í˜¸ì¶œì„ ìœ„í•œ ê³µí†µ ë˜í¼ í•¨ìˆ˜
 * @param {Function} apiCall - ì‹¤í–‰í•  API í•¨ìˆ˜
 * @param {string} context - ì—ëŸ¬ ë©”ì‹œì§€ì— í¬í•¨ë  ì»¨í…ìŠ¤íŠ¸ ì •ë³´
 * @param {Object} options - ì¶”ê°€ ì˜µì…˜
 * @param {boolean} options.showToast - ì—ëŸ¬ ì‹œ Toast í‘œì‹œ ì—¬ë¶€ (ê¸°ë³¸: true)
 * @param {boolean} options.logError - ì—ëŸ¬ ë¡œê¹… ì—¬ë¶€ (ê¸°ë³¸: true)
 * @param {string} options.fallbackValue - ì‹¤íŒ¨ ì‹œ ë°˜í™˜í•  ê¸°ë³¸ê°’
 */
export const safeApiCall = async (apiCall, context = '', options = {}) => {
  const {
    showToast = true,
    logError = true,
    fallbackValue = null
  } = options;

  try {
    log.debug(`API í˜¸ì¶œ ì‹œì‘: ${context}`, null, 'STORE');
    const result = await apiCall();
    log.debug(`API í˜¸ì¶œ ì„±ê³µ: ${context}`, result, 'STORE');
    return result;
  } catch (error) {
    if (logError) {
      log.error(`API í˜¸ì¶œ ì‹¤íŒ¨: ${context}`, error, 'STORE');
    }

    if (showToast && typeof window !== 'undefined') {
      const errorMessage = getStoreErrorMessage(context, error);
      toast.error('ì˜¤ë¥˜ ë°œìƒ', errorMessage);
    }

    if (fallbackValue !== null) {
      log.info(`í´ë°± ê°’ ì‚¬ìš©: ${context}`, fallbackValue, 'STORE');
      return fallbackValue;
    }

    throw error;
  }
};

