/**
 * Store ìƒíƒœ ë³€ê²½ ì‹œ ë¡œê¹…
 */
export const logStateChange = (storeName, stateName, oldValue, newValue) => {
  log.debug(
    `${storeName} ìƒíƒœ ë³€ê²½: ${stateName}`,
    { oldValue, newValue },
    'STORE-STATE'
