/**
 * ë¹„ë™ê¸° Store ì•¡ì…˜ì„ ìœ„í•œ ë˜í¼ (ë¡œë”© ìƒíƒœ í¬í•¨)
 */
export const withLoading = (set) => {
  return async (actionName, asyncAction) => {
    try {
      set({ loading: true, error: null });
      log.debug(`${actionName} ì‹œì‘`, null, 'STORE');

      const result = await asyncAction();

      log.debug(`${actionName} ì™„ë£Œ`, result, 'STORE');
      set({ loading: false });

      return result;
    } catch (error) {
      log.error(`${actionName} ì‹¤íŒ¨`, error, 'STORE');
      set({ loading: false, error: error.message });
      throw error;
    }
  };
};

/**
 * Store ì´ˆê¸°í™”ë¥¼ ìœ„í•œ í—¬í¼
 */
export const createStoreInitializer = (storeName, initialData = {}) => {
  return (set, get) => ({
    // ê³µí†µ ìƒíƒœ
    loading: false,
    error: null,

    // ì´ˆê¸° ë°ì´í„°
    ...initialData,

    // ê³µí†µ ì•¡ì…˜ë“¤
    setLoading: (loading) => {
      logStateChange(storeName, 'loading', get().loading, loading);
      set({ loading });
    },

    setError: (error) => {
      logStateChange(storeName, 'error', get().error, error);
      set({ error });
    },

    clearError: () => {
      set({ error: null });
    },

    // ìƒíƒœ ë¦¬ì…‹
    reset: () => {
      log.info(`${storeName} ìƒíƒœ ë¦¬ì…‹`, null, 'STORE');
      set({ ...initialData, loading: false, error: null });
    }
  });
};

